// Prepare Prometheus queries for weekly metrics
const queries = [
  { name: 'total_requests', query: 'sum(increase(api_gateway_requests_total[7d]))' },
  { name: 'avg_latency', query: 'avg(api_gateway_request_duration_seconds)' },
  { name: 'requests_by_model', query: 'sum by (model) (increase(api_gateway_requests_total[7d]))' },
  { name: 'total_cost', query: 'sum(api_gateway_cost_total)' },
  { name: 'error_rate', query: 'sum(rate(api_gateway_requests_total{status="error"}[7d]))' }
];

return queries;

// Generate comprehensive report
const data = $input.all();

// Extract metrics
const totalRequests = data.find(d => d.json.query?.includes('total_requests'));
const avgLatency = data.find(d => d.json.query?.includes('avg_latency'));
const requestsByModel = data.find(d => d.json.query?.includes('requests_by_model'));
const totalCost = data.find(d => d.json.query?.includes('total_cost'));
const errorRate = data.find(d => d.json.query?.includes('error_rate'));

const report = {
  period: 'Last 7 days',
  timestamp: new Date().toISOString(),
  metrics: {
    total_requests: parseFloat(totalRequests?.json.data?.result[0]?.value[1] || 0),
    avg_latency_seconds: parseFloat(avgLatency?.json.data?.result[0]?.value[1] || 0).toFixed(2),
    total_cost_usd: parseFloat(totalCost?.json.data?.result[0]?.value[1] || 0).toFixed(2),
    error_rate: parseFloat(errorRate?.json.data?.result[0]?.value[1] || 0).toFixed(4)
  },
  models: (requestsByModel?.json.data?.result || []).map(r => ({
    model: r.metric?.model || 'unknown',
    requests: parseFloat(r.value[1])
  }))
};

return report;

// Format report as readable text
const r = $json;

const text = `ğŸ“Š Weekly Performance Report

Period: ${r.period}
Generated: ${r.timestamp}

